import heapq

def find_median(arr1, arr2):
    n1 = len(arr1)
    n2 = len(arr2)
    if n1 > n2:
        i = 0
        j = n2 - 1
    else:
        i = n1 - 1
        j = -1

    heap = []
    heapq.heappush(heap, (arr1[i], i, 1))
