def find_top_2_elements(arr):
    count = {}
    for num in arr:
        if num in count:
            count[num] += 1
        else:
            count[num] = 1
    sorted_count = sorted(count.items(), key=lambda x: x[1], reverse=True)
    top_2_elements = []
    for i in range(2):
        top_2_elements.append(sorted_count[i][0])
    return top_2_elements

